import React from 'react';
import { connect } from "react-redux";
import EditWindow from '../../components/EditWindow/EditWindow';
import { start_POST_RequestServer, start_PUT_RequestServer, saveDataStore } from '../../store/serverViewType/actions/actionViewType';
import { linkViewType } from '../Navigation/Main';


const mapDispatchToProps ={
    saveDataStore,
    start_POST_RequestServer,
    start_PUT_RequestServer
}
const mapStateToProps = (state) => {
    return {
        initialProperties: state.WindowCreateNewData
    }
}

class ContEditWindow extends React.Component{
    constructor(props){
        super(props);

        this.state = props.initialProperties.stateData;
        this.nameWindow = props.initialProperties.nameWindow;
        this.labels = props.initialProperties.labels;
        this.placeholders = props.initialProperties.placeholders;
        this.typeData = props.initialProperties.typeData;
        
        this.saveDataStore = props.saveDataStore;
        this.start_POST_RequestServer = props.start_POST_RequestServer;
        this.start_PUT_RequestServer = props.start_PUT_RequestServer;
        
        this.typeRequestServer = props.initialProperties.typeRequest;

        this.linkAddress = linkViewType;
}

   
handlerOnChange = (event, keyName)=>
    this.setState({[keyName]: event.target.value})

nullStateApp = ()=>{
    let nullState = {};
    Object.keys(this.state).map((key)=> nullState[key] = '')
    this.setState({...nullState});
    }
 handlerClose = () => {
     let nullState = {};
     Object.keys(this.state).map((key) => nullState[key] = '');
     //this.saveDataStore({ ...nullState });

     this.setState({ ...nullState });
    }
  componentDidMount() {
        if (this.typeRequestServer === 'post') {
            this.nullStateApp();
        }
    }
handlerSave = ()=>{

    this.saveDataStore(this.state);
    
    if (this.typeRequestServer === 'post'){
        this.start_POST_RequestServer();
    }else 
        if(this.typeRequestServer === 'put'){
        this.start_PUT_RequestServer();
    }
    this.handlerClose();
    }


    render(){
    return(
        
            <EditWindow 
                    nameWindow = {this.nameWindow}
        
                    onchange = {this.handlerOnChange}
                    stateData = {this.state}
                    labels = {this.labels}
                    placeholders = {this.placeholders}
                    typeData={this.typeData}
                    to={this.linkAddress}
        
                    classNameLabel = ''
                    classNameInput = ''
                    classNameTextarea = ''
        
                    nameButtonSave = "Сохранить"
                    nameButtonClose = "Отмена"
                    onclick1 = {this.handlerSave}
                    onclick2 = {this.handlerClose}
                    classWrapper = ""
                    classButtonWrapper = ""
                    classButton = ""
                    classButtonX = ""
            />

    )
}
}



export default connect(mapStateToProps, mapDispatchToProps)(ContEditWindow)
