import React, { useState} from 'react';
import { connect, useDispatch } from "react-redux";
import ButtonLink from '../../UserInterface/Buttons/Button/ButtonLink';
import NavigationWindowBlind from '../../UserInterface/WindowBlind/NavigationWindowBlind';
import NavigationLinkAbout from '../../UserInterface/WindowBlind/NavigationLinkAbout';
import { startGetRequestServer } from '../../store/serverViewType/actions/actionViewType';
//import iceBaikal from '../../image/iceBaikal.jpg';
//import { NavLink } from 'react-router-dom';

import { Navigation } from "../../styles/Navigation.scss";
import { aboutLinks } from "../../constans/aboutLinks";


function Header() {

    const dispatch = useDispatch();

    const navStyle = ["Navigation"];
    const [linkAbout, setLinkAbout] = useState({
                                                text: "Ссылка не выбрана",
                                                classname:"navigationLinkAbout"})

    const hendlerHoverLink = (data) => {
        setLinkAbout({
                        text: aboutLinks[data],
                        classname: "navigationLinkAboutHover"
                    });
    }
    const hendlerLeaveLink = () => {
        setLinkAbout({
            text: "Ссылка не выбрана",
            classname: "navigationLinkAbout"
        });
    }

    return (
        <header>
            <nav className={navStyle.join(' ')}>
 
                    <ButtonLink
                        to='/'
                        name='Home'
                        onclick={() => console.log("GO HOME")}
                        onmouseover={() => hendlerHoverLink('Home')}
                        onmouseleave={hendlerLeaveLink}
                        classname='NavigationLink'
                        tabIndex='1'
                    />
                    <ButtonLink
                        to='/ViewType'
                        name='ViewType'
                        onclick={() => console.log("ViewType")}
                        onmouseover={() => hendlerHoverLink('ViewType')}
                         classname='NavigationLink'
                        tabIndex='2'
                    />
                    <ButtonLink
                        to='/EltypeClass'
                        name='EltypeClass'
                        onclick={() => console.log("EltypeClass")}
                        onmouseover={() => hendlerHoverLink('EltypeClass')}
                        classname='NavigationLink'
                        tabIndex='3'
                    />

            </nav>
            <NavigationWindowBlind onClick={() => console.log('NavigationWindowBlind')} />
            <NavigationLinkAbout
                aboutLink={linkAbout.text}
                classname={linkAbout.classname}
            />

        </header>
    )
}
export default connect()(Header)

