import React from 'react';

import RowCompareTable from './RowCompareTable';
import ButtonSmall from '../../UserInterface/Buttons/Button/ButtonSmall';
import withHoverDescription from '../../hoc/withHoverDescription';
import DescriptionButton from '../../components/Description/DescriptionButton';

function CompareTableEltypeClass(props) {
    const aboutDescription = {
        "SortList": "cортировать"
    }
    const haveDescription = true;

    // логика всплывающей подсказки спрятана в hoc, обертка кнопок и подсказка реализованы в ButtonConsoleTable
    const ButtonSort = withHoverDescription(ButtonSmall, DescriptionButton); 
    
    return(
        <div className={props.classname}>
            <table>
                  <thead>
                    <tr>
                        <th>

                            

                        </th>
                    {
                        props.columnName.map((name, index) => {
                            if (name === 'name' || name === 'isDeleted' || name === 'isTemplate') {
                                return <th
                                            key={index}
                                        >
                                            <div> {name} </div>

                                            <ButtonSort
                                                icon="fa fa-indent"
                                                onclick={props.onclickSortCompare}
                                                data={name}
                                                name="SortList"
                                                classnameDescription='DescriptionButton'
                                                aboutDescription={aboutDescription}
                                                haveDescription={haveDescription}
                                                positionDescription="top"
                                    />

                                        </th>
                            }
                        })
                    }
                    </tr>
                  </thead>

                  <tbody id="tablebody">
                    {
                        props.dataTable.map((data, index) => <RowCompareTable
                                                                    dataRow={data}
                                                                    key={index}
                                                                    onclick={props.onclick}
                                                                    onclickDelete={props.onclickDelete}
                                                            />
                                            )
                    }
                    </tbody>
            </table>

    </div>
    )
}

export default CompareTableEltypeClass
