import { takeEvery, put, call } from 'redux-saga/effects';
import {handlerDataSave} from '../../store/windowCreateNewData/actions/actionWindowCreateNewData';
import {START_PUT_REQUEST} from '../../store/windowCreateNewData/types';

const body_save = {
                uid: '111',
                name: "aaa",
                comment: "aaa",
                classViewProperty: "aaa",
                dbTable: "aaa"
            }

export function fetchData(){
    return fetch("http://localhost:5000/api/ViewType")
        fetch(this.url,{
                method: "PUT",
                body: JSON.stringify(body_save),
                headers: {'Content-Type': 'application/json'}   
                })
        .then(response => response.json())
}

export function* workerStart_PUT_RequestServer(){
     
    console.log('workerStart_PUT_RequestServer ');
    
    try {
        
        const data = yield call(fetchData);

        yield put(handlerDataSave(data));
        
    } catch (error) {
        alert('workerStart_PUT_RequestServer error ', error);
    }
 
}

export function* watchStart_PUT_RequestServer(){
    
    yield takeEvery(START_PUT_REQUEST, workerStart_PUT_RequestServer)
}
