import React, { useState } from 'react';

import NavigationWindowBlind from '../../UserInterface/Navigation/NavigationWindowBlind';
import NavigationToggle from '../../UserInterface/Navigation/NavigationToggle';

import withHoverDescription from '../../hoc/withHoverDescription';
import ButtonLink from '../../UserInterface/Buttons/Button/ButtonLink';
import ButtonLinkSmall from '../../UserInterface/Buttons/Button/ButtonLinkSmall';

import { aboutLinks } from "../../constans/aboutLinks";

function Header(props) {

    const [isOpen, setIsOpen] = useState(false);

    const navStyle = ["Navigation"];
    if (!isOpen) {
        navStyle.push("close");
    };

    const pages = [
        {
            to: '/',
            name: 'Home'
        },
        {
            to: '/EltypeClass',
            name: 'EltypeClass'
        },
        {
            to: '/ViewType',
            name: 'ViewType'
        },
    ]
    // логика всплывающей подсказки спрятана в обертку hoc hoverDescription, 
    // обертка кнопок и подсказка реализованы в ButtonNavigationSmall и ButtonNavigation
    const ButtonNavigationSmall = withHoverDescription(ButtonLinkSmall);
    const ButtonNavigation = withHoverDescription(ButtonLink);

    return (
        <header>
            <nav className={navStyle.join(' ')}>

                    <ButtonNavigationSmall
                                    to='/Personalize'
                                    icon="fa fa-user-circle-o"
                                    classname="PersonalizeButton"
                                    name='Personalize'
                                    onclick={() => setIsOpen(false)}
                                    tabindex="-1"
                                    aboutLinks={aboutLinks}
                    />
                    {
                        pages.map((page, index) => 
                                <ButtonNavigation
                                            to={page.to}
                                            name={page.name}
                                            onclick={() => setIsOpen(false)}
                                            classname='NavigationButtonLink'
                                            tabIndex={index + 1}
                                            key={index + 1}
                                            aboutLinks={aboutLinks}
                              />)
                    }
                
            </nav>
 
            {
                isOpen ?
                    <NavigationWindowBlind
                        onclick={() => setIsOpen(false)}
                        classname="NavigationWindowBlind"
                    />
                    : null
            }
            
            <NavigationToggle
                openNavigation={() => setIsOpen(!isOpen) }
                isOpen={isOpen}
                classname='NavigationToggle'
            />

        </header>
    )
}
export default Header
