import React, {useState, useEffect} from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import TableList from '../../components/Table/TableList';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import ContEditWindow from '../EditWindow/ContEditWindow'

import {startGetRequestServer} from '../../store/serverViewType/actions/actionViewType';
import {saveDataStore} from '../../store/windowCreateNewData/actions/actionWindowCreateNewData'

function ViewType (props){
    
    const dispatch = useDispatch();
    
    /* после загрузки GET запрос на сервер и перерисовка таблицы */
    useEffect( ()=>{
        dispatch(startGetRequestServer());
    }, [])
    
    /*для заполнения таблицы */
    const stateTable = useSelector(state => state.stateTable);
    
    /*для заполнения всплывающего окна */
    const currentStateData = useSelector(state => state.WindowCreateNewData.stateData);
  
    const [flag, setFlag] = useState(false);
    const [typeRequest, setTypeRequest] = useState(null);
    
    const windowOpen = ()=>{
        setFlag(!flag);
    }
    
    /*определение по какой строке таблицы был клик */
    const handlerTableRowUID = (event, data)=>{
        event.preventDefault();
        
        stateTable.map( (obj, index) => {
            if (obj.uid === data){
                console.log("index ", index);
                dispatch(saveDataStore(obj));
            }
        })
    }
    const openWindowAddData = ()=>{
        setTypeRequest('post');
        windowOpen();
    }
    const openWindowUpdateData = ()=>{
        setTypeRequest('put');
        windowOpen();
    }
    /*заглушка на кнопку удаления */
    const handlerClick = ()=>{
        console.log('click');
    }
    const handlerWindowClose = ()=>{
        windowOpen();
        let nullState = {};
        Object.keys(currentStateData).map((key)=> nullState[key] = '');
        dispatch(saveDataStore({...nullState}));
    }
    
    return(
        <div>
            <HeaderDictionary 
                    classname= ""
                    nameHeader = "Вывод данных View Type"
                />
        
            <ConsoleButton
                classname = ""
        
                name1 = "Обновить"
                title1 = "Обновить"
                onclick1 = {()=>{ dispatch(startGetRequestServer())} }
                

                name2 = "Добавить"
                title2 = "Добавить"
                onclick2 = {openWindowAddData}

                name3 = "Внести изменения"
                title3 = "Внести изменения"
                onclick3 = {openWindowUpdateData}

                name4 = "Только активные"
                title4 = "Только активные"
                onclick4 = {handlerClick}

                name5 = "Удалить"
                title5 = "Удалить"
                onclick5 = {handlerClick}

                type = "button"
                tabindex = "-1"

                />
                    
            <TableList 
                    columnName={Object.keys(stateTable[0])}
                    dataTable = {stateTable}
                    onclick = {(event, data) => handlerTableRowUID(event, data)}
                />
        
        {
            flag?
            <ContEditWindow 
                    typeRequestServer = {typeRequest}
                    handlerClose = {handlerWindowClose}/>
            :null
        
        }
        </div>
    )
}

export default connect()(ViewType)
