import React from 'react';
import InputAuth from '../../UserInterface/InputAuth/InputAuth';



class LoginAuth extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            login: localStorage.getItem('login'),
            password: ''
        };
        this.dataInputAuth = {

            login: {
                type: 'text',
                placeholder: 'login',
                autofocus: false
            },
            password: {
                type: 'password',
                placeholder: 'пароль',
                autofocus: false
            }
        };
        
    }

    onchange = (event, nameInput) => {
        this.setState({
            ...this.state,
            [nameInput]: event.target.value
        });
    }
     
    onclick = nameInput => {
        this.setState({
            ...this.state,
            [nameInput]: ''
        });
    }

    hendlerSave = () => {
        console.log(' войти в аккаунт ', this.state);
        localStorage.setItem('login', this.state.login);
    }

    hendlerInitialRegistration = () => {
        console.log(' первичная регистрация ', this.state);
    }

    render(){
        return (
            <div>
                <h2>Начать работу :</h2>

                {
                    Object.keys(this.dataInputAuth).map((keyState, index) => {
                        return <InputAuth
                                        value={this.state[keyState]}
                                        key = { index }
                                        id={keyState}
                                        tabindex={index}
                                        {...this.dataInputAuth[keyState]}
                                        onchange={this.onchange}
                                        onclick={this.onclick}
                                    />
                    })
                }

                <button
                    onClick={this.hendlerSave}
                >
                    Войти
                </button>
 
                <button
                    onClick={this.hendlerInitialRegistration}
                >
                    Первичная регистрация
                </button>

            </div>

            )
    }
    
}

export default LoginAuth
