import {
    SHOW_LOADER
                } from "../types";

const initialState = {
    loading: false
};


const stateFlags = (state = initialState, action) => {

    switch (action.type) {

        case SHOW_LOADER:
            return {
                ...state,
                loading: action.payload
            }

        default:
            return state;
    }
}
export default stateFlags
