import { takeEvery, put, call } from 'redux-saga/effects';
import {handlerButtonSave} from '../../store/windowCreateNewData/actions/actionWindowCreateNewData';
import {START_PUT_REQUEST} from '../../store/windowCreateNewData/types';

export function fetchData(){
    return fetch("http://localhost:5000/api/ViewType")
        .then(response => response.json())
}

export function* workerStartGetRequestServer(){
     
    console.log('workerStartGetRequestServer ');
    
    try {
        
        const data = yield call(fetchData);

        yield put(getRequestServer(data));
        
    } catch (error) {
        alert('workerStartGetRequestServer error ', error);
    }
    
   
}

export function* watchStart_PUT_RequestServer(){
    
    yield takeEvery(START_PUT_REQUEST, workerStart_PUT_RequestServer)
}
