import React from 'react';

class RowTable extends React.Component{
    constructor(props) {
        super(props);
        this.Keys = Object.keys(this.props.dataRow);
        this.uid = this.props.dataRow.uid;
        this[props.dataRow.uid] = React.createRef();
    }


    inputChecked = data => {
        if (data === true) {
            return 'checked'
        } else {
            return false
        };
    }


    render() {
        return(

            <tr >
                {
                    (this.props.number <= this.props.CountRow)?
                        <td> {this.props.number}</td>
                        :null
                }
            
                {
                    this.Keys.map((keyObj, index) => {

                        if (this.props.number <= this.props.CountRow) {

                                if (keyObj === 'name') {
                                    return <td
                                        key={index}
                                        uid={this.props.dataRow.uid}
                                        onClick={(event) => this.props.onclick(event, this.uid)}
                                        onContextMenu={(event) => this.props.onclick(event, this.uid)}


                                            >
                                        {this.props.dataRow[keyObj]}

                                    </td>
                                }

                                if (keyObj === 'isDeleted' || keyObj === 'isTemplate') {
                                    return <td
                                            key={index}
                                            uid={this.props.dataRow.uid}
                                            onClick={(event) => this.props.onclick(event, this.uid)}
                                            onContextMenu={(event) => this.props.onclick(event, this.uid)}

                                    >
                                        <input
                                            type="checkbox"
                                            checked={this.inputChecked(this.props.dataRow[keyObj])}
                                            disabled="disabled"

                                        /><span className="fa fa-check" aria-hidden="true"></span>
                                    </td>
                                }
                        }

                    
                    }) 
                }
        
            </tr>
        )
    }

}

export default RowTable
