import React, { useState } from 'react';


function ButtonSmallRowTable (props){

    const data = props.data;


    const buttonFalse = {
        flag: false,
        icon: "fa fa-times-circle",
        name: 'DeleteRow',
        func: props.funcDelete
    };

    const [flagButton, setBFlagBatton] = useState(true);

    return(       
                <div 
                    className= {props.classname}

                    tabIndex = {props.tabindex}
        >    
            {
                flagButton ?
                    <span
                            className="fa fa-filter fa-lg"
                            aria-hidden="true" 
                            onClick={(event) => props.funcAdd(event, data)}
                            name = 'addCompare'
                    >
                    </span>
                    : <span
                            className=" fa fa-times-circle"
                            aria-hidden="true"
                            onClick={(event) => props.funcDelete(event, data)}
                            name='DeleteRow'
                    >
                    </span>

            }
                    

                </div>
    )
}

export default ButtonSmallRowTable
