import {GET_REQUEST, POST_REQUEST, PUT_REQUEST, START_GET_REQUEST} from "../types";

const getRequestServer = (dataFromServer)=>{
    
    return {
        type: GET_REQUEST,
        payload: dataFromServer
    }
}

const postRequestServer = (dataFromServer)=>{
    
    return {
        type: POST_REQUEST,
        payload: dataFromServer
    }
}

const putRequestServer = (dataFromServer, oldStateTable)=>{
    
        oldStateTable.map ( obj =>{
        if (obj.uid == dataFromServer.uid){
            obj = dataFromServer
        }
            return obj
    })
    
    return {
        type: PUT_REQUEST,
        payload: dataFromServer
    }
}

const startGetRequestServer = ()=>{
    
        return {
        type: START_GET_REQUEST
    }
    
}

export {getRequestServer, postRequestServer, putRequestServer, startGetRequestServer}
