import React from 'react';
import '../../styles/NavigationToggle.scss';


const NavigationToggle = props => {

    const cls = [
        'NavigationToggle',
        'fa'
    ]

    props.isOpen ? cls.push('fa-times fa-2x open') : cls.push('fa-bars fa-2x')

    return (

        <span
            className={cls.join(' ')}
            onClick={props.openNavigation}
            aria-hidden="true"
        />

    )
}

export default NavigationToggle
