import React from 'react';
import { connect } from 'react-redux';

import InputAuth from '../../UserInterface/InputAuth/InputAuth';
import { linkPersonalize } from '../Navigation/Main';
import Button from '../../UserInterface/Buttons/Button/Button';
import ButtonLink from '../../UserInterface/Buttons/Button/ButtonLink';

function mapStateToProps(state) {
    return {
        stateLogin: state.stateAuthorization.user.login,
        statePassword: state.stateAuthorization.user.password
    }
} 

class LoginAuthChange extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            passwordOld: '',
            login: '',
            passwordNewFirst: '',
            passwordNewSecond: '',
            helloName: '',
            flagWarning: false,
            warningPasswordOld: false,
            warningPasswordNewFirst: false,
            warningPasswordNewSecond: false
        };
        this.dataInputAuth = {

            passwordOld: {
                type: 'password',
                placeholder: 'действующий пароль',
                autofocus: true
            },
            passwordNewFirst: {
                type: 'password',
                placeholder: 'новый пароль',
                autofocus: false
            },
            passwordNewSecond: {
                type: 'password',
                placeholder: 'подтвердите пароль',
                autofocus: false
            }
        };
        
    }

    helloLogin = () => {
        let loginLocalStorage = localStorage.getItem('login');
        if (localStorage.getItem('login') === null) { loginLocalStorage = '' };
        this.setState({
            login: loginLocalStorage,
            helloName: localStorage.getItem('nameAuth')
        })
    }

    onchange = (event, nameInput) => {
        this.setState({
            [nameInput]: event.target.value
        });
    }
     
    onclick = nameInput => {
        this.setState({
            [nameInput]: ''
        });
    }

    hendlerValidation = () => {
        console.log('проверить новый пароль  ', this.state);

        let flag = false;

        if (this.state.passwordOld !== this.statePassword) {
            this.setState({
                warningPasswordOld: true
            });
            flag = true;
        };
        if (this.state.passwordNewFirst === '' || this.state.passwordNewFirst === null || this.state.passwordNewFirst === undefined) {
            this.setState({
                warningPasswordNewFirst: true
            })
            flag = true;
        };
        if (this.state.passwordNewFirst !== this.state.passwordNewSecond) {
            this.setState({
                warningPasswordNewSecond: true
            })
            flag = true;
        };
        this.setState({
            flagWarning: flag
        })

    }


    render() {

        return (
            <div>
                <h2> Изменить пароль входа в систему </h2>
                <h3> Для login {this.props.stateLogin}</h3>

                {
                    Object.keys(this.dataInputAuth).map((keyState, index) => {
                        return <InputAuth
                                        value={this.state[keyState]}
                                        key = { index }
                                        id={keyState}
                                        tabindex={index}
                                        {...this.dataInputAuth[keyState]}
                                        onchange={this.onchange}
                                        onclick={this.onclick}
                                    />
                    })
                }


                <Button
                    onclick={this.hendlerValidation}
                    name="Проверить новый пароль"
                    icon="fa fa-floppy-o"
                />

                <ButtonLink
                    onclick={this.hendlerSave}
                    to={linkPersonalize}
                    name="Назад"
                    icon="fa fa-sign-out"
                />
                {
                    this.state.flagWarning ?
                        <div>Ошибка ввода пароля</div>
                        :<div>Пароль введен верно</div>
                }

            </div>

            )
    }
    
}

export default connect(mapStateToProps)(LoginAuthChange)
