import React, { useEffect } from 'react';
import { connect,  useSelector, useDispatch } from "react-redux";

import {startGetRequestServer } from '../../store/serverViewType/actions/actionViewType';
import TableList from '../../components/Table/TableList';
import {saveDataStore} from '../../store/windowCreateNewData/actions/actionWindowCreateNewData'


function ContTable (props){
    
    const dispatch = useDispatch();
    
    useEffect( ()=>{
        dispatch(startGetRequestServer());
    }, [])
   
    const stateTable = useSelector(state => state.stateTable);
    
    const handlerUID = (event, data)=>{
        event.preventDefault();
        
        stateTable.map( (obj, index) => {
            if (obj.uid === data){
                console.log("index ", index);
                dispatch(saveDataStore(obj));
            }
        })
    }
    
    return(
        <TableList 
                    columnName={Object.keys(stateTable[0])}
                    dataTable = {stateTable}
                    onclick = {(event, data) => handlerUID(event, data)}
            />
    )
}

export default  connect()(ContTable)
