import React from 'react';
import { connect } from "react-redux";

import { tablebodyBackground_Coffee, rowFocus_Coffee } from '../styles/const_color';

import {
    startGetRequestServerEltypeClass,
    saveDataStore
} from '../store/EltypeClass/actions/actionEltypeClass';
import {
    changeNumberList,
    getDataPageRowTable
} from '../store/EltypeClass/actions/actionsFlagsEltypeClass';


const mapStateToProps = (state) => {
    return {
//        activList: state.stateFlagsEltypeClass.dataListTable, // отображаемый лист
//        compareArr: state.compareDataEltypeClass, // массив строк, избранных для сравнения
//        focusData: state.createDataEltypeClass.stateData,
        countRow: state.stateFlagsEltypeClass.countRowTable, // количество строк на отображаемой странице
//        numberList: state.stateFlagsEltypeClass.numberList //номер отображаемого листа
        }
    }
const mapDispatchToProps = {
    saveDataStore,
    changeNumberList
}

const withSearch = (Component, data) => {

 
    class ComponentWithSort extends React.Component {
        constructor(props) {
            super(props);
            this.numRowOnPage = 0; // номер строки объекта на отображаемой странице (в дальнейшем выделим цветом)
        }
        hendlerSearch = (event) => {
            event.preventDefault();
            if (event.target.value === '') { return }; //если пусто, выходим
            let num; // номер выбранного объекта в массиве

            data.map((obj, index) => {

                if (obj.name === event.target.value) {
                    num = index + 1;
                    this.numRowOnPage = index + 1;
                    this.props.saveDataStore(obj); // сохранить фокус в хранилище
                }
            });
            //поиск номера страницы
            let i = this.props.countRow;
            let numList = 1;
            if (num === undefined) { return }; //если пусто, выходим

            while (i < num) {
                i = i + this.props.countRow;
                this.numRowOnPage = this.numRowOnPage - this.props.countRow;
                numList++;
            }

            this.props.changeNumberList(numList); //сохраняем номер
            this.props.getDataPageRowTable(data, this.props.countRow, numList);// загружаем выбранный лист

            const tablebody = document.querySelector('tbody');

            for (let child of tablebody.children) {
                child.style.background = tablebodyBackground_Coffee;//убираем цвет ранее выбранной строки
            };

            tablebody.children[this.numRowOnPage - 1].style.background = rowFocus_Coffee; // выделяем цветом
            tablebody.children[this.numRowOnPage - 1].children[1].focus();
        }

        componentDidMount() {

        }

        render() {
            let props = {
                ...this.props,
    
            }
            return (
                <Component {...props} />
                )
        }
    }
    return connect(mapStateToProps, mapDispatchToProps)(ComponentWithSort)
}
export default withSearch
















