import { takeEvery, put, call } from 'redux-saga/effects';
import {getRequestServer} from '../../store/serverViewType/actions/actionViewType';
import {START_GET_REQUEST} from '../../store/serverViewType/types';
import {url} from "../../constans/url"

export function fetchData(){
    return fetch(url)
        .then(response => response.json())
}

export function* workerStartGetRequestServer(){
     
    console.log('workerStartGetRequestServer ');
    
    try {
        
        const data = yield call(fetchData);

        yield put(getRequestServer(data));
        
    } catch (error) {
        alert('workerStartGetRequestServer error ', error);
    }
    
   
}

export function* watchStartGetRequestServer(){
    
    yield takeEvery(START_GET_REQUEST, workerStartGetRequestServer)
}
