                this.activArr = compareArr
            } else { return }
        }

        componentDidUpdate(prevProps) {
            if (this.props.focusData !== prevProps.focusData) {
                focusRowColorBackground(this.activArr, this.props.focusData);
            }
        }
        //componentDidMount() {
        //    focusRowColorBackground(activList, this.props.focusData);
        //}
        render() {
            let props = {
                ...this.props,
                onclick: this.handlerTableRowUID
            }
            return (
                <Component {...props} />
                )
        }
    }
    return connect(mapStateToProps, mapDispatchToProps)(ComponentWithClickSaveFocus)
}
