import React from 'react';
import { connect } from "react-redux";
import { bindActionCreators } from 'redux'
import EditWindow from '../../components/EditWindow/EditWindow';
import {handlerButtonSave} from '../../store/windowCreateNewData/actions/actionWindowCreateNewData'
const mapDispatchToProps = dispatch => ({
  handlerButtonSave: bindActionCreators(handlerButtonSave, dispatch)
})
const mapStateToProps = (state) => {
    return {
        initialProperties: state.WindowCreateNewData
    }
}


class ContEditWindow extends React.Component{
    constructor(props){
        super(props);
        
        this.state = props.initialProperties;
        this.nameWindow = "Новые данные"
        this.labels = [
                        'uid',
                        'Наименование',
                        'Примечания',
                        'classViewProperty',
                        'dbTable'
                    ];
        this.placeholders = [
                        'uid',
                        'Введите полное наименование',
                        'Дополнения и комментарии',
                        'classViewProperty',
                        'dbTable'
                    ];
        this.typeData = [
                        "number",
                        "text",
                        "text",
                        "text",
                        "text"
                    ];
        this.handlerButtonSave = props.handlerButtonSave;
        this.refInput1 = React.createRef();
}


//  componentDidMount() {
//    this.refInput1.current.focus();
//  }

    
handlerOnChange = (event, keyName)=>
    this.setState({[keyName]: event.target.value})

handlerClick = ()=>{
    console.log('handlerClick = this.state ', this.state);
    this.handlerButtonSave(this.state);
}


    render(){
    return(
        <EditWindow 
                    nameWindow = {this.nameWindow}
        
                    onchange = {this.handlerOnChange}
                    stateData = {this.state}
                    labels = {this.labels}
                    placeholders = {this.placeholders}
                    typeData ={this.typeData}
        

                    ref = {this.refInput1}

        
                    classNameLabel = ''
                    classNameInput = ''
                    classNameTextarea = ''
        
                    nameButtonSave = "Сохранить"
                    nameButtonClose = "Отмена"
                    onclick1 = {this.handlerClick}
                    onclick2 = {this.handlerClick}
                    classWrapper = ""
                    classButtonWrapper = ""
                    classButton = ""
                    classButtonX = ""
            />
    )
}
}



export default connect(mapStateToProps, mapDispatchToProps)(ContEditWindow)


