import {
    ADD_DATA_COMPARE,
    CLEAR_ARR_COMPARE

} from "../types";

export const addDataCompareArr = (data, arrOld) => {
    
    let arr = [];
    for (let i = 0; i < arrOld.length; i++) {
        if (arrOld[i].uid != data.uid && arrOld[i] != {}) {
            arr.push(arrOld[i]);
        }
    };
    arr.push(data);

                return {
                    type: ADD_DATA_COMPARE,
                    payload: arr
                };

};
