import React, { useState} from 'react';
import InputDatalist from '../../UserInterface/Input/InputDatalist';
import Checkbox from '../../UserInterface/Input/Checkbox';
import { countries } from '../../constans/countries';
import Photogallery from '../../components/Photogallery/Photogallery';
import { image } from '../../constans/ImageGallery/image';

function Home (props){

    const [flag, setFlag] = useState(true);

    const inputChecked = () => {
        if (flag == true) {
            return 'checked'
        } else {
            return false
        };
    }
    const hendlerHaveDescription = (event) => {
        event.PreventDefault();
        setFlag(!flag);
    }

    return(
         <div>
                <div className="">
                    HOME
                </div>

            <InputDatalist
                arrValue={countries}
                id="countriesInput"
                listId="countriesListId"
                placeholder="country"
                onchange={() => console.log('Input coutry')}
                tabindex="1"
                autocomplete = "off"
            />
            <Checkbox
                label = "Показать всплывающие подсказки"
                id="haveDescription"
                type="checkbox"
                data={flag}
                onchange={hendlerHaveDescription}
                checked={inputChecked}
                classNameLabel = ''
                classNameCheckbox='beautiful-Checkbox'
            />
            <Photogallery
                imageArr={image}
               
            />
                
        </div>
    )
}

export default Home
