import React, {useState} from 'react';
import { connect, useDispatch, useSelector } from "react-redux";
import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import ContHeaderDictionary from '../Header/ContHeaderDictionary'
import ContTable from '../Table/ContTable'
import ContEditWindow from '../EditWindow/ContEditWindow'

import {startGetRequestServer} from '../../store/serverViewType/actions/actionViewType';
import {saveDataStore} from '../../store/windowCreateNewData/actions/actionWindowCreateNewData'

function ViewType (props){
    
    const dispatch = useDispatch();
    
    const handlerClick = ()=>{
        console.log('click');
    }

    const [flag, setFlag] = useState(false);
    
    const windowOpen = ()=>{
        setFlag(!flag);
    }
    const handlerWindowClose = ()=>{
        windowOpen();
        dispatch(saveDataStore({}));
    }
    
    return(
        <div>
            <ContHeaderDictionary />
        
            <ConsoleButton
                classname = ""
        
                name1 = "Обновить"
                title1 = "Обновить"
                onclick1 = {()=>{ dispatch(startGetRequestServer())} }
                

                name2 = "Добавить"
                title2 = "Добавить"
                onclick2 = {windowOpen}

                name3 = "Внести изменения"
                title3 = "Внести изменения"
                onclick3 = {windowOpen}

                name4 = "Только активные"
                title4 = "Только активные"
                onclick4 = {handlerClick}

                name5 = "Удалить"
                title5 = "Удалить"
                onclick5 = {handlerClick}

                type = "button"
                tabindex = "-1"

                />
                    
            <ContTable />
        
        {
            flag?
            <ContEditWindow 
                    handlerClose = { handlerWindowClose}/>
            :null
        
        }
        </div>
    )
}

export default connect()(ViewType)
