import {GET_REQUEST, POST_REQUEST, PUT_REQUEST, START_GET_REQUEST} from "../types";

export const getRequestServer = (dataFromServer)=>{
    
    return {
        type: GET_REQUEST,
        payload: dataFromServer
    }
}

export const postRequestServer = (dataFromServer)=>{
    
    return {
        type: POST_REQUEST,
        payload: dataFromServer
    }
}

export const putRequestServer = (dataFromServer, oldStateTable)=>{
    
    let newStateTable = oldStateTable.map ( (obj, index) =>{
        if (obj.uid == dataFromServer.uid){
                obj = dataFromServer;
            }
                return obj
        })
    
    return {
        type: PUT_REQUEST,
        payload: newStateTable
    }
}

export const startGetRequestServer = ()=>{
    
        return {
        type: START_GET_REQUEST
    }
    
}
