import {
    SHOW_LOADER,
    COUNTER_PAGES_TABLE,
    COUNT_ROW_TABLE,
    PAGE_ROW_TABLE,
    CHANGE_NUMBER_LIST
                    } from "../types";

export const loadingShow = (data) => {

    return {
        type: SHOW_LOADER,
        payload: data
    }
}

export const getCounterPagesTable = (dataFromServer, countRowTable) => {

    let sumRow = dataFromServer.length + 1;
    let i = 0;

    while (sumRow >= countRowTable) {
        sumRow = sumRow - countRowTable;
        i++;
    }

    if (sumRow <= 0) {
        return {
            type: COUNTER_PAGES_TABLE,
            payload: i
        }
    } else {
        i++;

        return {
            type: COUNTER_PAGES_TABLE,
            payload: i
        }
    }
}


//export const changeCountRowTable = (data) => {

//    return {
//        type: COUNT_ROW_TABLE,
//        payload: data
//    }
//}
export const changeNumberList = (data) => {

    return {
        type: CHANGE_NUMBER_LIST,
        payload: data
    }
}

export const getDataPageRowTable = (dataFromServer, countRow, num) => {

    let activeRowMin = countRow * (num - 1) + 1;
    let activeRowMax = countRow * num;

    let dataList = [];
    let numbersList = [];
    dataFromServer.map((obj, index) => {
        let number = index + 1;
        if (number >= activeRowMin && number <= activeRowMax) {
            dataList.push(obj);
            numbersList.push(number)
        };
    })
        return {
            type: PAGE_ROW_TABLE,
            payload: dataList
            //payload: {
            //            countRowTable: [dataList],
            //            numberRowTable: [numbersList]
            //        }
        }
}
