import React, { useState, useEffect } from 'react';


function ButtonSmallRowTable (props){

    const data = props.data;


    const [flagButton, setFlagBatton] = useState(true);

    const funcAdd = (event) => {
        props.funcAdd(event, data);
        setFlagBatton(prevState => !flagButton);
    }
    const funcDelete = (event) => {
        props.funcDelete(event, data);
        setFlagBatton(prevState => !flagButton);
    }

    useEffect(() => {
        //if (flagButton === true) {

        //    setStyleRow((prevState) => {
        //        return { ...styleRow, background: `${props.colorFocusRowBackground}`, 'fontWeight': '600' };
        //    })

        console.log('flagButton ', flagButton)
    }, [])
    return(       
                <div 
                    className= {props.classname}

                    tabIndex = {props.tabindex}
        >    
            {
                flagButton ?
                    <span
                            className="fa fa-filter fa-lg"
                            aria-hidden="true" 
                            onClick={funcAdd}
                            name = 'addCompare'
                    >
                    </span>
                    : <span
                            className=" fa fa-times-circle"
                            aria-hidden="true"
                            onClick={funcDelete}
                            name='DeleteRow'
                    >
                    </span>

            }
                    

                </div>
    )
}

export default ButtonSmallRowTable
