import { tablebodyBackground_Coffee, rowFocus_Coffee } from '../styles/const_color';

const allRowColorBackground = tablebodyBackground_Coffee; //цвет поля таблицы
const colorFocusRowBackground = rowFocus_Coffee; //цвет выбранной в фокус строки

export const focusRowColorBackground = (dataFocus) => {

    const tablebody = document.querySelector('tbody');

    for (let child of tablebody.children) {
        child.style.background = allRowColorBackground;//убираем цвет ранее выбранной строки

        if (child.children[1].innerHTML !== undefined && child.children[1].innerHTML === dataFocus.uid) {
            child.style.background = colorFocusRowBackground; //выделяем цветом строку фокуса
        };
    };
}
