import {
            SHOW_LOADER,
            STATUS_CONFIRM_DELETING,
            STATUS_OPEN_EDIT_WINDOW,
            STATUS_WARNING
                        } from "../types";

const initialState = {
                loading: false,
                confirmDeleting: false,
                openEditWindow: false,
                warning: false
            };


const stateFlagsEltypeClass = (state = initialState, action) => {

    switch (action.type) {

        case SHOW_LOADER:
            return {
                ...state,
                loading: action.payload
            }
        case STATUS_CONFIRM_DELETING:
            return {
                ...state,
                confirmDeleting: action.payload
            }
        case STATUS_OPEN_EDIT_WINDOW:
            return {
                ...state,
                openEditWindow: action.payload
            }
        case STATUS_WARNING:
            return {
                ...state,
                warning: action.payload
            }

        default:
            return state;
    }
}
export default stateFlagsEltypeClass
