import {
    SHOW_LOADER,
    HIDE_LOADER
                    } from "../types";

export const loadingShow = (data) => {

    return {
        type: SHOW_LOADER,
        payload: data
    }
}

export const loadingHide = () => {

    return {
        type: HIDE_LOADER
    }
}
