import React from 'react';
import { connect, useSelector } from "react-redux";

import RowTable from './RowTable';
import ButtonSmall from '../../UserInterface/Buttons/Button/ButtonSmall';
import withHoverDescription from '../../hoc/withHoverDescription';
import DescriptionButton from '../../components/Description/DescriptionButton';
import FooterTable from '../FooterTable/FooterTable';


function TableList(props) {

    const stateDescription = useSelector(state => state.stateDescription); //все подсказки
    const haveDescription = true;

    // логика всплывающей подсказки спрятана в hoc, обертка кнопок и подсказка реализованы в ButtonConsoleTable
    const ButtonSort = withHoverDescription(ButtonSmall, DescriptionButton);

    return(
        <div id='tableListBaseWrapper' className={props.classname}>
            <table>
                  <thead>
                    <tr>
                        <th>
                            №
                        </th>

                    {
                        props.columnName.map((name, index) => {
                            if (name === 'name' || name === 'isDeleted' || name === 'isTemplate') {
                                return <th
                                            key={index}
                                        >
                                            <div>{name}</div>

                                            <ButtonSort
                                                    icon="fa fa-arrow-down"
                                                    onclick={props.onclickSortBase}
                                                    data={name}
                                                    name="SortBase"
                                                    classnameDescription='DescriptionButton'
                                                    aboutDescription={stateDescription}
                                                    haveDescription={haveDescription}
                                                    positionDescription="top"
                                                />
                                            <ButtonSort
                                                    icon="fa fa-indent"
                                                    onclick={props.onclickSortList}
                                                    data={name}
                                                    name="SortList"
                                                    classnameDescription='DescriptionButton'
                                                    aboutDescription={stateDescription}
                                                    haveDescription={haveDescription}
                                                    positionDescription = "top"
                                                />

                                        </th>
                            }
                        })
                    }
                    </tr>
                  </thead>

                  <tbody id="tablebody">
                    {
                        props.dataTable.map((data, index) => <RowTable
                                                                        dataRow={data}
                                                                        key={index}
                                                                        number={index + 1}
                                                                        onclick={props.onclick}
                                                                        CountRow={props.CountRow}                                        
                                                            />
                                            )
                    }
                    </tbody>
            </table>
            <FooterTable
                classname={props.classnameConsoleCountRow}
                classnameButtonConsoleCountRow={props.classnameButtonConsoleCountPow}
                onclickOpenPagesTable={props.onclickOpenPagesTable}
                onclickChangeButtonConsole={props.onclickChangeButtonConsole}
                onclickInputOpen={props.onclickInputOpen}
                onclickInputSave={props.onclickInputSave}
                onchange={props.onchangeCountRow}
                CountRow={props.CountRow}
                counterPages={props.counterPages}
                activArrNumberPages={props.activArrNumberPages}
                flagCountRow={props.flagCountRow}
                length={props.length}
                numberActivList={props.numberActivList}
            />
    </div>
    )
}

export default connect()(TableList)
