import React, { useState } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ButtonLinkSmallNotMouse from '../../UserInterface/Buttons/Button/ButtonLinkSmallNotMouse';
import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import CompareTableEltypeClass from '../../components/Table/CompareTableEltypeClass';

import { linkEltypeClass } from '../Navigation/Main';
import { linkEditWindowEltypeClass } from '../Navigation/Main';
import { linkWindowConfirmDeleting } from '../Navigation/Main';


function CompareEltypeClass(props) {

    const linkAddress = linkEltypeClass;
    const linkAddressWindowEdit = linkEditWindowEltypeClass;
    const linkAddressWindowDelete = linkWindowConfirmDeleting;


    const compareArr = useSelector(state => state.compareDataEltypeClass); // массив строк, избранных для сравнения

    const [flagCompareTableOpen, setFlagCompareTableOpen] = useState(true);

    const hendlerOpenWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }
    const hendlerClick = () => { }
    const hendlerDelete = () => { }

    if (compareArr.length <= 1) {
        setFlagCompareTableOpen(false);
    };
    const stateConsoleButton = [
  
        {
            text: "Внести изменения",
            icon: "fa fa-pencil-square-o fa-lg",
            onclick: () => hendlerOpenWindow("Внести изменения", "put"),
            link: linkAddressWindowEdit
        },
        {
            text: "Удалить",
            icon: "fa fa-times fa-lg",
            onclick: () => { },
            link: linkAddressWindowDelete
        }
    ];

    return (
        <div className="DataBase__container">

            <div className="DataBase__header__container">

                <ConsoleButton
                    classnameWrapper="DataBase__header__console"

                    stateConsoleButton={stateConsoleButton}

                    onmouseover={HendlerOnMouseover}
                    onmouseleave={HendlerOnMouseLeave}

                />

                <HeaderDictionary
                    classname="DataBase__header__header"
                    nameHeader="Сравнить избранное: Классы для Типов связей и Типов сущностей"
                />

                <ButtonLinkSmallNotMouse
                    onclick={() => { }}
                    to={linkAddress}
                    icon="fa fa-times"
                />
            </div>


            {
                flagCompareTableOpen ?
                        <CompareTableEltypeClass
                                columnName={Object.keys(compareArr[1])}
                                dataTable={compareArr}
                                onclick={hendlerClick}
                                onclickDelete={hendlerDelete}
                            />
                        :<h1> Недостаточно значений для сравнения</h1>
            }
        

        </div>
    )
}

export default connect()(CompareEltypeClass)
